mapper.select:
SELECT *
FROM member
WHERE mem_id = #{memId}
AND mem_pw = #{memPw}


SELECT *
FROM (
  SELECT ROWNUM rnum, p.*
  FROM (
    SELECT *
    FROM member
    ORDER BY mem_id
  ) p
  WHERE ROWNUM <= #{endNum}
)
WHERE rnum >= #{startNum}

mapper.update:
UPDATE member
SET
mem_pw = #{memPw}
WHERE mem_id=#{memId}

mapper.select:
SELECT COUNT(*)
FROM member
WHERE mem_id = #{memId}
